CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;
CREATE TABLE "AttributeTypes" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" character varying(100) NOT NULL,
    "DisplayName" character varying(100) NOT NULL,
    "SortOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_AttributeTypes" PRIMARY KEY ("Id")
);

CREATE TABLE "AuditLogs" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserId" integer,
    "EventType" character varying(50) NOT NULL,
    "Action" character varying(100) NOT NULL,
    "Details" text NOT NULL,
    "IpAddress" character varying(45) NOT NULL,
    "UserAgent" character varying(500),
    "Timestamp" timestamp with time zone NOT NULL,
    "IntegrityHash" character varying(100) NOT NULL,
    "IsArchived" boolean NOT NULL DEFAULT FALSE,
    "ArchivedAt" timestamp with time zone,
    "RowVersion" bytea,
    CONSTRAINT "PK_AuditLogs" PRIMARY KEY ("Id")
);

CREATE TABLE "Categories" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" character varying(100) NOT NULL,
    "Slug" character varying(200) NOT NULL,
    "Description" text,
    "SortOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_Categories" PRIMARY KEY ("Id")
);

CREATE TABLE "DiscountCodes" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Code" character varying(50) NOT NULL,
    "Percentage" numeric(5,2) NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_DiscountCodes" PRIMARY KEY ("Id")
);

CREATE TABLE "ElasticsearchOutboxMessages" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "EntityType" character varying(100),
    "EntityId" character varying(100),
    "OperationType" character varying(50),
    "Payload" text,
    "CreatedAt" timestamp with time zone NOT NULL,
    "IsProcessed" boolean NOT NULL,
    "ProcessedAt" timestamp with time zone,
    "Document" text,
    "ChangeType" character varying(50),
    "RetryCount" integer NOT NULL,
    "Error" text,
    CONSTRAINT "PK_ElasticsearchOutboxMessages" PRIMARY KEY ("Id")
);

CREATE TABLE "FailedElasticOperations" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "EntityType" character varying(100) NOT NULL,
    "EntityId" character varying(100) NOT NULL,
    "Document" text NOT NULL,
    "Error" text NOT NULL,
    "Status" character varying(50) NOT NULL,
    "RetryCount" integer NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "ProcessedAt" timestamp with time zone,
    "LastRetryAt" timestamp with time zone,
    CONSTRAINT "PK_FailedElasticOperations" PRIMARY KEY ("Id")
);

CREATE TABLE "OrderStatus" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" character varying(50) NOT NULL,
    "DisplayName" character varying(100) NOT NULL,
    "Icon" character varying(100),
    "Color" character varying(50),
    "SortOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "IsDefault" boolean NOT NULL,
    "AllowCancel" boolean NOT NULL,
    "AllowEdit" boolean NOT NULL,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_OrderStatus" PRIMARY KEY ("Id")
);

CREATE TABLE "OutboxMessages" (
    "Id" uuid NOT NULL,
    "Type" character varying(500) NOT NULL,
    "Payload" text NOT NULL,
    "OccurredAt" timestamp with time zone NOT NULL,
    "ProcessedAt" timestamp with time zone,
    "Error" character varying(2000),
    CONSTRAINT "PK_OutboxMessages" PRIMARY KEY ("Id")
);

CREATE TABLE "RateLimits" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Key" character varying(256) NOT NULL,
    "Count" integer NOT NULL,
    "LastAttempt" timestamp with time zone NOT NULL,
    "ExpiresAt" timestamp with time zone NOT NULL,
    "RowVersion" bytea,
    CONSTRAINT "PK_RateLimits" PRIMARY KEY ("Id")
);

CREATE TABLE "Shippings" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" character varying(100) NOT NULL,
    "Description" character varying(500),
    "BaseCost" numeric(18,2) NOT NULL,
    "EstimatedDeliveryTime" character varying(100),
    "MinDeliveryDays" integer NOT NULL,
    "MaxDeliveryDays" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "SortOrder" integer NOT NULL,
    "IsDefault" boolean NOT NULL,
    "MinOrderAmount" numeric,
    "MaxOrderAmount" numeric,
    "MaxWeight" numeric,
    "IsFreeAboveAmount" boolean NOT NULL,
    "FreeShippingThreshold" numeric,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_Shippings" PRIMARY KEY ("Id")
);

CREATE TABLE "Users" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "PhoneNumber" character varying(15) NOT NULL,
    "FirstName" character varying(50),
    "LastName" character varying(50),
    "Email" character varying(256),
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_Users" PRIMARY KEY ("Id")
);

CREATE TABLE "WalletReconciliationAudits" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "WalletId" integer NOT NULL,
    "UserId" integer NOT NULL,
    "SnapshotBalance" numeric NOT NULL,
    "LedgerBalance" numeric NOT NULL,
    "Delta" numeric NOT NULL,
    "DetectedAt" timestamp with time zone NOT NULL,
    CONSTRAINT "PK_WalletReconciliationAudits" PRIMARY KEY ("Id")
);

CREATE TABLE "Wallets" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserId" integer NOT NULL,
    "CurrentBalance" numeric(18,2) NOT NULL,
    "ReservedBalance" numeric(18,2) NOT NULL,
    "Status" character varying(20) NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "RowVersion" bytea,
    CONSTRAINT "PK_Wallets" PRIMARY KEY ("Id"),
    CONSTRAINT "CK_Wallet_CurrentBalance_NonNegative" CHECK ("CurrentBalance" >= 0),
    CONSTRAINT "CK_Wallet_ReservedBalance_NonNegative" CHECK ("ReservedBalance" >= 0)
);

CREATE TABLE "Warehouses" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Code" character varying(50) NOT NULL,
    "Name" character varying(100) NOT NULL,
    "City" character varying(100) NOT NULL,
    "Address" character varying(500),
    "Phone" character varying(20),
    "IsActive" boolean NOT NULL,
    "IsDefault" boolean NOT NULL,
    "Priority" integer NOT NULL,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "RowVersion" bytea,
    CONSTRAINT "PK_Warehouses" PRIMARY KEY ("Id")
);

CREATE TABLE "AttributeValues" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "AttributeTypeId" integer NOT NULL,
    "Value" character varying(100) NOT NULL,
    "DisplayValue" character varying(100) NOT NULL,
    "HexCode" character varying(50),
    "SortOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_AttributeValues" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_AttributeValues_AttributeTypes_AttributeTypeId" FOREIGN KEY ("AttributeTypeId") REFERENCES "AttributeTypes" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Brands" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" character varying(100) NOT NULL,
    "Slug" character varying(200) NOT NULL,
    "Description" character varying(500),
    "CategoryId" integer NOT NULL,
    "SortOrder" integer NOT NULL DEFAULT 0,
    "IsActive" boolean NOT NULL DEFAULT TRUE,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_Brands" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Brands_Categories_CategoryId" FOREIGN KEY ("CategoryId") REFERENCES "Categories" ("Id") ON DELETE RESTRICT
);

CREATE TABLE "DiscountRestriction" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "DiscountCodeId" integer NOT NULL,
    "Type" character varying(50) NOT NULL,
