    CONSTRAINT "PK_DiscountRestriction" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_DiscountRestriction_DiscountCodes_DiscountCodeId" FOREIGN KEY ("DiscountCodeId") REFERENCES "DiscountCodes" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Carts" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
