    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "RowVersion" bytea,
    CONSTRAINT "PK_Notifications" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Notifications_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Orders" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserId" integer NOT NULL,
    "UserAddressId" integer,
    "ReceiverName" character varying(100) NOT NULL,
    "AddressSnapshot" jsonb NOT NULL,
    "TotalAmount" numeric(18,2) NOT NULL,
    "TotalProfit" numeric(18,2) NOT NULL,
    "ShippingCost" numeric(18,2) NOT NULL,
    "DiscountAmount" numeric(18,2) NOT NULL,
    "FinalAmount" numeric(18,2) NOT NULL,
    "Status" character varying(50) NOT NULL,
    "ShippingId" integer NOT NULL,
    "DiscountCodeId" integer,
    "PaymentDate" timestamp with time zone,
    "ShippedDate" timestamp with time zone,
    "DeliveryDate" timestamp with time zone,
    "IdempotencyKey" character varying(256) NOT NULL,
    "OrderNumber" character varying(50) NOT NULL,
    "CancellationReason" character varying(500),
    "CancelledBy" integer,
    "IsDeleted" boolean NOT NULL,
    "DeletedAt" timestamp with time zone,
    "DeletedBy" integer,
    "CreatedAt" timestamp with time zone NOT NULL,
    "UpdatedAt" timestamp with time zone,
    "OrderStatusId" integer,
    "RowVersion" bytea,
    CONSTRAINT "PK_Orders" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Orders_OrderStatus_OrderStatusId" FOREIGN KEY ("OrderStatusId") REFERENCES "OrderStatus" ("Id"),
    CONSTRAINT "FK_Orders_Shippings_ShippingId" FOREIGN KEY ("ShippingId") REFERENCES "Shippings" ("Id") ON DELETE RESTRICT,
    CONSTRAINT "FK_Orders_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Tickets" (
