    "CancelledAt" timestamp with time zone,
    "RowVersion" bytea,
    CONSTRAINT "PK_DiscountUsage" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_DiscountUsage_DiscountCodes_DiscountCodeId" FOREIGN KEY ("DiscountCodeId") REFERENCES "DiscountCodes" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_DiscountUsage_Orders_OrderId" FOREIGN KEY ("OrderId") REFERENCES "Orders" ("Id") ON DELETE RESTRICT,
    CONSTRAINT "FK_DiscountUsage_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE RESTRICT
);

CREATE TABLE "PaymentTransactions" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
