    "Quantity" integer NOT NULL,
    "PurchasePriceAtOrder" numeric(18,2) NOT NULL,
    "SellingPriceAtOrder" numeric(18,2) NOT NULL,
    "OriginalPriceAtOrder" numeric(18,2) NOT NULL,
